import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import './App.css';

function Sidebar() {
  const navigate = useNavigate();
  
  return (
    <aside className="sidebar">
      <div className="sidebar-icons">
        <div className="icon-wrapper">
          <i className="fas fa-user-friends" onClick={() => navigate('/')}></i>
          <div className="tooltip">Profile</div>
        </div>
        <div className="icon-wrapper">
          <i className="fas fa-file-alt"></i>
          <div className="tooltip">Resume</div>
        </div>
        <div className="icon-wrapper">
          <i className="fas fa-layer-group" onClick={() => navigate('/portfolio')}></i>
          <div className="tooltip">Portfolio</div>
        </div>
        <div className="icon-wrapper">
          <i className="fas fa-clipboard-list"></i>
          <div className="tooltip">Experience</div>
        </div>
        <div className="icon-wrapper">
          <i className="fas fa-paper-plane"></i>
          <div className="tooltip">Contact</div>
        </div>
      </div>
    </aside>
  );
}

function Profile({ onTabChange, activeTab }) {
  return (
    <section className="profile-section">
      <img className="avatar" src="https://avatars.githubusercontent.com/u/9919?s=200&v=4" alt="Profile" />
      <h2>Sahiba Ansari</h2>
      <div className="subtitle">Human Software Engineer</div>
      <div className="profile-actions">
        <button className="btn btn-green">View Portfolio</button>
        <button className="btn btn-pink">View Resume</button>
      </div>
      <div className="bio">
        <b>SHORT BIO</b>
        <p>Hi, I'm Sahiba Ansari — a passionate frontend developer with one year of hands-on experience building responsive, user-focused web applications. I specialize in designing sleek, modern interfaces that not only function smoothly but leave a lasting impression.</p>
        <p>When I'm not turning lines of code into beautiful UI, you'll find me exploring the latest in AI — using smart tools to boost efficiency, spark creativity, and sometimes just to see what tech can surprise me next.</p>
        <p>I'm always excited to bring ideas to life through clean code and thoughtful design. Let's team up and build something meaningful — or at the very least, something that works like magic and looks even better.</p>
      </div>
      <div className="tabs-wrapper">
        <div className="tabs-full-line"></div>
        <div className="tabs">
          <button className={activeTab === 'skills' ? 'tab active' : 'tab'} onClick={() => onTabChange('skills')}>My Skills</button>
          <button className={activeTab === 'experience' ? 'tab active' : 'tab'} onClick={() => onTabChange('experience')}>Experience</button>
        </div>
      </div>
    </section>
  );
}

function Education() {
  return (
    <section className="card education-card">
      <h3>Education</h3>
      <div>
        <b>Bachelor of Science (B.Sc.)</b>
        <ul>
          <li>Computer Engineering</li>
          <li>Deen Dayal Upadhayaya Gorakhpur University</li>
          <li>CGPA: 8.1 [2021 – 2024]</li>
        </ul>
      </div>
    </section>
  );
}

function Contact() {
  return (
    <section className="card contact-card">
      <h3>👋 Thanks for visiting my <br/> site!</h3>
      <div>Glad to have you here.</div>
      <div className="connect">Let's Connect</div>
      <div className="social-icons">
        <a href="https://github.com/SAHIBAKHATOON"><i className="icon-github"></i></a>
        <a href="https://www.linkedin.com/in/ansari-sahiba-094691260/"><i className="icon-linkedin"></i></a>
        <a href="#"><i className="icon-mail"></i></a>
        <a href="#"><i className="icon-whatsapp"></i></a>
      </div>
    </section>
  );
}

function Skills() {
  const skillCategories = [
    {
      title: "Front End",
      skills: [
        { name: "HTML", icon: "🌐", color: "#FFD700" },
        { name: "CSS", icon: "🎨", color: "#FFD700" },
        { name: "JavaScript", icon: "JS", color: "#FFD700" },
        { name: "React.JS", icon: "⚛", color: "#FFD700" },
        { name: "Next.js", icon: "⚡", color: "#FFD700" },
        { name: "Tailwind CSS", icon: "🎯", color: "#FFD700" },
        { name: "Bootstrap", icon: "B", color: "#FFD700" },
        { name: "XML", icon: "</>", color: "#FFD700" }
      ]
    },
    
    {
      title: "Tools & Others",
      skills: [
        { name: "Git", icon: "📦", color: "#4ECDC4" },
        { name: "VS Code", icon: "💻", color: "#4ECDC4" },
        { name: "Figma", icon: "🎨", color: "#4ECDC4" },
        { name: "Cursor AI", icon: "🖱️", color: "#4ECDC4" },
        { name: "npm", icon: "📦", color: "#4ECDC4" },
        { name: "GitHub", icon: "🐙", color: "#4ECDC4" }
      ]
    }
  ];

  return (
    <div className="skills-content">
      {skillCategories.map((category, categoryIndex) => (
        <div key={categoryIndex} className="skill-category">
          <h3 className="skill-category-title">{category.title}</h3>
          <div className="skill-tags">
            {category.skills.map((skill, skillIndex) => (
              <div 
                key={skillIndex} 
                className="skill-tag"
                style={{ borderColor: skill.color }}
              >
                <span className="skill-icon">{skill.icon}</span>
                <span className="skill-name">{skill.name}</span>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

function Experience() {
  const experiences = [
    {
      title: "Frontend Developer Intern",
      company: "SalesqueenSoftware Sloutions",
      date: "April 2024 - Oct 2024",
      color: "#2db7f5",
      bullets: [
        "Led a team of 10 frontend developers, managing the overall workflow, task distribution, and code reviews. Focused on building clean, responsive user interfaces while ensuring the team followed best practices in UI/UX and frontend development.",
        "Successfully completed the project as the lead intern, showcasing strong leadership and project management skills.",
        "Utilized technologies including HTML, CSS, JavaScript and Bootstrap 5 to deliver a robust and scalable solution.",
        "Gained hands-on experience in frontend web development and team coordination."
      ]
    },
    {
      title: "Blockchain Developer Intern",
      company: "Ranchimall Javascript Internshala",
      date: "November 2024 - January 2025",
      color: "#6c63ff",
      bullets: [
        "Worked as a Blockchain Developer, where I implemented a JavaScript-based functionality to fetch and display the total USDT balance from a user's wallet. This feature solved a key issue in the project by ensuring accurate and real-time visibility of wallet funds.",
        "Applied frontend technologies such as HTML, CSS to create responsive and interactive user interfaces.",
        "Collaborated with a team to implement features and enhance the overall user experience of the application."
      ]
    }
  ];

  return (
    <div className="experience-timeline">
      {experiences.map((exp, idx) => (
        <div className="timeline-item" key={idx}>
          <div className="timeline-dot" style={{ background: exp.color }}></div>
          <div className="timeline-content">
            <div className="timeline-title">{exp.title}</div>
            <div className="timeline-company">
              <b>{exp.company}</b>
              <span className="timeline-date">{exp.date}</span>
            </div>
            <ul>
              {exp.bullets.map((b, i) => (
                <li key={i}>{b}</li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}

function Portfolio() {
  const [activeFilter, setActiveFilter] = useState('All');
  
  const projects = [
    {
      id: 1,
      title: "Portfolio Site",
      description: "A personal portfolio website showcasing my skills and projects.",
      role: "Front-end Developer",
      tech: "React.JS + Vite | Bootstrap",
      client: "Personal project",
      category: "Web-App",
      image: "https://via.placeholder.com/300x200/23272f/ffffff?text=Portfolio+Site"
    },
    {
      id: 2,
      title: "Bulk Resume Scanner",
      description: "NLP and ML-powered web app matching resumes to jobs.",
      role: "Python Developer",
      tech: "Flask | SpaCy | Bootstrap | MySQL",
      client: "Personal Project",
      category: "Software",
      image: "https://via.placeholder.com/300x200/23272f/ffffff?text=Bulk+Resume+Scanner"
    },
    {
      id: 3,
      title: "Batch Management System",
      description: "Batch Management System with Class Scheduling, Attendance Tracking.",
      role: "PHP Developer",
      tech: "PHP | Bootstrap | MySQL",
      client: "Personal project",
      category: "Software",
      image: "https://via.placeholder.com/300x200/23272f/ffffff?text=Batch+Management"
    },
    {
      id: 4,
      title: "Event Weavers",
      description: "Event Management with QR Based Attendance",
      role: "PHP Developer",
      tech: "PHP | Bootstrap | MySQL",
      client: "Internship Project",
      category: "Web-App",
      image: "https://via.placeholder.com/300x200/23272f/ffffff?text=Event+Weavers"
    },
    {
      id: 5,
      title: "Gemini Clone",
      description: "AI-powered chat interface similar to Google Gemini.",
      role: "Front-end Developer",
      tech: "React.JS | OpenAI API | CSS",
      client: "Personal project",
      category: "Web-App",
      image: "https://via.placeholder.com/300x200/23272f/ffffff?text=Gemini+Clone"
    },
    {
      id: 6,
      title: "Dental Care",
      description: "Healthcare website for dental services and appointments.",
      role: "Front-end Developer",
      tech: "React.JS | Bootstrap | CSS",
      client: "Internship Project",
      category: "Web-App",
      image: "https://via.placeholder.com/300x200/23272f/ffffff?text=Dental+Care"
    },
    {
      id: 7,
      title: "Book Rental System",
      description: "Library management system for book rentals and tracking.",
      role: "PHP Developer",
      tech: "PHP | MySQL | Bootstrap",
      client: "Personal project",
      category: "Software",
      image: "https://via.placeholder.com/300x200/23272f/ffffff?text=Book+Rental"
    },
    {
      id: 8,
      title: "Billing CRUD",
      description: "Billing and invoice management system with CRUD operations.",
      role: "PHP Developer",
      tech: "PHP | MySQL | Bootstrap",
      client: "Personal project",
      category: "Software",
      image: "https://via.placeholder.com/300x200/23272f/ffffff?text=Billing+CRUD"
    }
  ];

  const filteredProjects = activeFilter === 'All' 
    ? projects 
    : projects.filter(project => project.category === activeFilter);

  return (
    <div className="portfolio-page">
      <div className="portfolio-header">
        <h1>My Projects</h1>
        <p>Here are some of my recent works</p>
      </div>
      
      <div className="portfolio-filters">
        <button 
          className={activeFilter === 'All' ? 'filter-btn active' : 'filter-btn'}
          onClick={() => setActiveFilter('All')}
        >
          All
        </button>
        <button 
          className={activeFilter === 'Web-App' ? 'filter-btn active' : 'filter-btn'}
          onClick={() => setActiveFilter('Web-App')}
        >
          Web-App
        </button>
        <button 
          className={activeFilter === 'Android App' ? 'filter-btn active' : 'filter-btn'}
          onClick={() => setActiveFilter('Android App')}
        >
          Android App
        </button>
        <button 
          className={activeFilter === 'Software' ? 'filter-btn active' : 'filter-btn'}
          onClick={() => setActiveFilter('Software')}
        >
          Software
        </button>
      </div>

      <div className="projects-grid">
        {filteredProjects.map(project => (
          <div key={project.id} className="project-card">
            <div className="project-image">
              <img src={project.image} alt={project.title} />
            </div>
            <div className="project-content">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <div className="project-details">
                <div className="detail-item">
                  <span className="detail-label">Role:</span>
                  <span className="detail-value">{project.role}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">Tech:</span>
                  <span className="detail-value">{project.tech}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">Client:</span>
                  <span className="detail-value">{project.client}</span>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function HomePage() {
  const [activeTab, setActiveTab] = useState('experience');
  
  return (
    <main className="main-content">
      <div className="main-left">
        <Profile onTabChange={setActiveTab} activeTab={activeTab} />
        {activeTab === 'skills' ? <Skills /> : <Experience />}
      </div>
      <div className="main-right">
        <Education />
        <Contact />
      </div>
    </main>
  );
}

function App() {
  return (
    <Router>
      <div className="app-layout">
        <Sidebar />
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/portfolio" element={<Portfolio />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
