import React, { useEffect } from 'react';

function ResumePage() {
  const resumeUrl = 'https://docs.google.com/document/d/13VOb0QdC9aR8H3qq2RbKiRtrLzGCfFcZ/edit#heading=h.1fob9te';

  useEffect(() => {
    // Automatically redirect to the Google Docs resume
    window.open(resumeUrl, '_blank');
  }, []);

  return (
    <div className="resume-page" style={{
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px'
    }}>
      <div className="resume-container" style={{
        textAlign: 'center',
        background: 'rgba(255, 255, 255, 0.1)',
        backdropFilter: 'blur(10px)',
        borderRadius: '20px',
        padding: '40px',
        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        maxWidth: '500px',
        width: '100%'
      }}>
        <div className="resume-content">
          <h1 className="resume-title" style={{
            fontSize: '2.5rem',
            marginBottom: '20px',
            background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            Resume
          </h1>
          <div className="resume-message" style={{ color: 'white', lineHeight: '1.6' }}>
            <p style={{ fontSize: '1.1rem', marginBottom: '15px' }}>Redirecting you to view my resume...</p>
            <p style={{ fontSize: '1rem', marginBottom: '25px', opacity: '0.9' }}>If the page doesn't open automatically, please click the button below:</p>
            <button 
              className="btn btn-primary"
              onClick={() => window.open(resumeUrl, '_blank')}
              style={{
                background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)',
                border: 'none',
                borderRadius: '25px',
                padding: '12px 30px',
                color: 'white',
                fontSize: '1rem',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                boxShadow: '0 4px 15px rgba(0, 0, 0, 0.2)'
              }}
              onMouseOver={(e) => {
                e.target.style.transform = 'translateY(-2px)';
                e.target.style.boxShadow = '0 6px 20px rgba(0, 0, 0, 0.3)';
              }}
              onMouseOut={(e) => {
                e.target.style.transform = 'translateY(0)';
                e.target.style.boxShadow = '0 4px 15px rgba(0, 0, 0, 0.2)';
              }}
            >
              <i className="fas fa-file-alt" style={{ marginRight: '8px' }}></i>
              View Resume
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ResumePage;
