import React, { useState } from 'react';
import './App.css';

function Sidebar() {
  return (
    <aside className="sidebar">
      <div className="sidebar-icons">
        <i className="fas fa-user-friends"></i>
        <i className="fas fa-file-alt"></i>
        <i className="fas fa-layer-group"></i>
        <i className="fas fa-clipboard-list"></i>
        <i className="fas fa-paper-plane"></i>
      </div>
    </aside>
  );
}

function Profile({ onTabChange, activeTab }) {
  return (
    <section className="profile-section">
      <img className="avatar" src="https://avatars.githubusercontent.com/u/9919?s=200&v=4" alt="Profile" />
      <h2>Sahiba Ansari</h2>
      <div className="subtitle">Human Software Engineer</div>
      <div className="profile-actions">
        <button className="btn btn-green">View Portfolio</button>
        <button className="btn btn-pink">View Resume</button>
      </div>
      <div className="bio">
        <b>SHORT BIO</b>
        {/* <p>Hi, I'm Sahiba Ansari, a freshly minted software engineer with a passion for building digital wonders. With skills in software, web app development, I can help you shape your ideas into reality (or at least make them look really cool).</p>
        <p>When I'm not coding, I'm probably exploring new ways to use AI to boost my productivity (or just playing with AI-powered toys).</p>
        <p>Let's team up and create something amazing â€“ or at least something that doesn't crash every five minutes.</p> */}
        <p>Hi, I'm Sahiba Ansari â€” a passionate frontend developer with one year of hands-on experience building responsive, user-focused web applications. I specialize in designing sleek, modern interfaces that not only function smoothly but leave a lasting impression.</p>
        <p>When Iâ€™m not turning lines of code into beautiful UI, youâ€™ll find me exploring the latest in AI â€” using smart tools to boost efficiency, spark creativity, and sometimes just to see what tech can surprise me next.</p>
        <p>I'm always excited to bring ideas to life through clean code and thoughtful design. Letâ€™s team up and build something meaningful â€” or at the very least, something that works like magic and looks even better.</p>

      </div>
      <div className="tabs-wrapper">
        <div className="tabs-full-line"></div>
        <div className="tabs">
          <button className={activeTab === 'skills' ? 'tab active' : 'tab'} onClick={() => onTabChange('skills')}>My Skills</button>
          <button className={activeTab === 'experience' ? 'tab active' : 'tab'} onClick={() => onTabChange('experience')}>Experience</button>
        </div>
      </div>
    </section>
  );
}

function Education() {
  return (
    <section className="card education-card">
      <h3>Education</h3>
      <div>
        <b>Bachelor of Science (B.Sc.)</b>
        <ul>
          <li>Computer Engineering</li>
          <li>Deen Dayal Upadhayaya Gorakhpur University</li>
          <li>CGPA: 8.1 [2021 â€“ 2024]</li>
        </ul>
      </div>
      {/* <div>
        <b>Diploma in Engineering</b>
        <ul>
          <li>Computer Engineering</li>
          <li>Gujarat Technological University</li>
          <li>GGPA: 8.38 [2018 â€“ 2021]</li>
        </ul>
      </div> */}
    </section>
  );
}

function Contact() {
  return (
    <section className="card contact-card">
      <h3>ðŸ‘‹ Thanks for visiting my <br/> site!</h3>
      <div>Glad to have you here.</div>
      <div className="connect">Let's Connect</div>
      <div className="social-icons">
        <a href="https://github.com/SAHIBAKHATOON"><i className="icon-github"></i></a>
        <a href="https://www.linkedin.com/in/ansari-sahiba-094691260/"><i className="icon-linkedin"></i></a>
        <a href="#"><i className="icon-mail"></i></a>
        <a href="#"><i className="icon-whatsapp"></i></a>
      </div>
    </section>
  );
}

// function Blogs() {
//   return (
//     <section className="card blog-card">
//       <h3>Check Out My Amazing Blogs!</h3>
//       <div>I write amazing stuff that you don't want to miss.</div>
//     </section>
//   );
// }

function Skills() {
  return (
    <div className="skills-content">Skills content goes here...</div>
  );
}

function Experience() {
  const experiences = [
    {
      title: "Frontend Developer Intern",
      company: "SalesqueenSoftware Sloutions",
      date: "April 2024 - Oct 2024",
      color: "#2db7f5",
      bullets: [
        "Led a team of 10 frontend developers, managing the overall workflow, task distribution, and code reviews. Focused on building clean, responsive user interfaces while ensuring the team followed best practices in UI/UX and frontend development.",
        "Successfully completed the project as the lead intern, showcasing strong leadership and project management skills.",
        "Utilized technologies including HTML, CSS, JavaScript and Bootstrap 5 to deliver a robust and scalable solution.",
        "Gained hands-on experience in  frontend web development,  and team coordination."
      ]
    },
    {
      title: "Software Developer Intern",
      company: "Ranchimall Javascript Internshala",
      date: "July 2023 - Aug 2023",
      color: "#6c63ff",
      bullets: [
        "Contributed to the development of the Dental Care application using React JS.",
        "Applied frontend technologies such as HTML, CSS, and React JS to create responsive and interactive user interfaces.",
        "Collaborated with a team to implement features and enhance the overall user experience of the application."
      ]
    },
    // Add more experiences as needed
  ];

  return (
    <div className="experience-timeline">
      {experiences.map((exp, idx) => (
        <div className="timeline-item" key={idx}>
          <div className="timeline-dot" style={{ background: exp.color }}></div>
          <div className="timeline-content">
            <div className="timeline-title">{exp.title}</div>
            <div className="timeline-company">
              <b>{exp.company}</b>
              <span className="timeline-date">{exp.date}</span>
            </div>
            <ul>
              {exp.bullets.map((b, i) => (
                <li key={i}>{b}</li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}

export default function App() {
  const [activeTab, setActiveTab] = useState('experience');
  return (
    <div className="app-layout">
      <Sidebar />
      <main className="main-content">
        <div className="main-left">
          <Profile onTabChange={setActiveTab} activeTab={activeTab} />
          {activeTab === 'skills' ? <Skills /> : <Experience />}
        </div>
        <div className="main-right">
          <Education />
          <Contact />
          {/* <Blogs /> */}
        </div>
      </main>
    </div>
  );
}
